import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import { CardList } from './components/card-list/card-list.component';

 export class App extends Component {

constructor(props) {
  super(props)

  this.state = {
    monsters :[],
    searchField:''
  };
}
componentDidMount(){
  fetch('https://jsonplaceholder.typicode.com/users')
  .then(response =>response.json())
  //.then(users=> console.log(users))
  .then(users => this.setState({monsters :users}))
}



render(){
  const {monsters,searchField }=this.state;
  const filterMonster=monsters.filter(monster => monster.name.toLoweCase().includes(searchField.toLocaleLowerCase())
  )
return(
    <div className='App'>
    <input type="search" placeholder='search monstor' onChange={e =>{this.setState({searchField:e.target.value},()=>console.log(this.state))}}
  />
    <CardList monsters={this.state.monsters} />


    </div>
    );
}


}
export default App;
